    private void barreMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_barreMouseClicked
        // TODO add your handling code here:
        erreurBarre.setText("");
        int erreur=0;
        
        if(Treillis.ListeNoeuds.size()<2){
            erreur+=1;
            erreurBarre.setText("Vous devez déjà avoir créer au moins 2 noeuds !");
        }
        
        if (erreur==0){
            FrameBarre barre = new FrameBarre();
            barre.setVisible(true);
        }
    }//GEN-LAST:event_barreMouseClicked

