    private void noeudMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_noeudMouseClicked
        // TODO add your handling code here:
        int erreur=0;
        erreurNoeud.setText("");
        
        if (Treillis.ListeTriangle.isEmpty()==true){
            erreur+=1;
            erreurNoeud.setText("Vous devez d'abord créer un triangle !");
        } 
        
        if (erreur==0){
            FrameNoeud noeud = new FrameNoeud();
            noeud.setVisible(true);
        }
    }//GEN-LAST:event_noeudMouseClicked

    private void calculerMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_calculerMouseClicked
        // TODO add your handling code here:
        Treillis.matrix.resoud();
    }//GEN-LAST:event_calculerMouseClicked

